import React from 'react';
import {ProjectData} from "./ProjectData";
// TODO: comments schrijven
export default class AdminProjectMenu extends React.Component {

    closeMenu = (e) => {
        this.props.changeHandler(e);
    }

    render() {
        return (
            <>
                <form method='POST' className='min-w-[360px] absolute max-w-[520px] flex flex-row bg-light rounded-md'>
                    <div className='flex flex-col px-2 py-2.5'>
                        <input type='text' placeholder='Titel' className='w-full h-[24px] mb-3 rounded-md text-color bg-[#4b4e69] border border-[#406380]'/>
                        <input type='date' className='w-full h-[24px] mb-3 rounded-md text-color bg-[#4b4e69] border border-[#406380]'/>
                        <input type='date' className='w-full h-[24px] mb-3 rounded-md text-color bg-[#4b4e69] border border-[#406380]'/>

                        {ProjectData.map((Create, i) => {
                            return (
                                <>
                                    <div className='group'>
                                        <div className='flex py-2 px-2.5 text-sm text-color group-hover:bg-[#062640]'>
                                            <span className='w-3/5'>{Create.title}</span>
                                            {Create.values.length > 1 ? (
                                                <select className='w-2/5 text-[#8b9cd0] group-hover:text-[#bca9ff]'>
                                                    {Create.values.map((option, i) => {
                                                        return (
                                                            <option value={option}>{option}</option>
                                                        )
                                                    })}
                                                </select>
                                                ) :
                                                Create.values.map((option, i) => {
                                                    return (
                                                        <span className='w-2/5'>{option}</span>
                                                    )
                                                })}
                                        </div>
                                    </div>
                                </>
                            )
                        })}

                    </div>
                </form>
            </>
        )
    }
}
