import React from 'react';
import  AdminGroupsButtons  from './AdminGroupsButtons';
import { AdminGroupsEdit } from './AdminGroupsEdit';

export class AdminGroups extends React.Component {
    constructor(props) {
        super(props);

        this.state = {groups: false}
        this.toggleGroups = this.toggleGroups.bind(this)
    }
    toggleGroups() {
        const groups = this.state.groups
        // set this.state.open to true/false
        this.setState( {
            groups: !groups
        })
    }
    closeGroups = (e) => {
        this.props.changeHandler(e);
    }

    render() {
        return (
            <div className={"flex flex-col bg-div lg:w-[40%] w-full px-5 py-5 rounded-sm"}>
                <div className={"flex justify-around"}>
                    <div className={"flex flex-col px-2 py-2"}>
                        <select className={"border-2 px-2 py-2 bg-div"}>
                            <option value="sd20">sd20</option>
                            <option value="sd21">sd21</option>
                            <option value="sd22">sd22</option>
                        </select>
                        <button className={"border-2 px-2 py-2"}>create group</button>
                    </div>

                    <AdminGroupsButtons/>

                </div>

                {this.state.groups ? <AdminGroupsEdit/> : null}


            </div>
        )
    }
}
